ATE`() using utf8mb4)) and isnull(`o_1`.`data`)),`o`.`total`,0)) + sum(if(((convert(date_format(`o`.`data`,'%Y-%m') using utf8mb4) = convert(`MYDATE`() using utf8mb4)) and (`o_1`.`data` is not null)),`o`.`total`,0)))) * 100) AS `% Fechados / Gerados`,((sum(if(((convert(date_format(`v`.`data`,'%Y-%m') using utf8mb4) = convert(`MYDATE`() using utf8mb4)) and (`o`.`status` = 'FECHADO')),`o`.`total`,0)) / ((sum(if(((convert(date_format(`o`.`data`,'%Y-%m') using utf8mb4) < convert(`MYDATE`() using utf8mb4)) and ((`o`.`status` = 'ATIVO') or (`o`.`status` = 'EXPIRADO') or ((`o`.`status` = 'FECHADO') and (convert(date_format(`v`.`data`,'%Y-%m') using utf8mb4) >= convert(`MYDATE`() using utf8mb4))) or ((`o`.`status` = 'PERDIDO') and (convert(date_format(`o`.`lastUpdated`,'%Y-%m') using utf8mb4) >= convert(`MYDATE`() using utf8mb4))))),`o`.`total`,0)) + sum(if(((convert(date_format(`o`.`data`,'%Y-%m') using utf8mb4) = convert(`MYDATE`() using utf8mb4)) and isnull(`o_1`.`data`)),`o`.`total`,0))) + sum(if(((convert(date_format(`o`.`data`,'%Y-%m') using utf8mb4) = convert(`MYDATE`() using utf8mb4)) and (`o_1`.`data` is not null)),`o`.`total`,0)))) * 100) AS `% Fechados / Carteira` from ((((`staccato`.`orcamento` `o` left join `staccato`.`loja` `l` on((`o`.`idLoja` = `l`.`idLoja`))) left join `staccato`.`usuario` `u` on((`o`.`idUsuario` = `u`.`idUsuario`))) left join `staccato`.`orcamento` `o_1` on((`o`.`replicadoDe` = `o_1`.`idOrcamento`))) left join `staccato`.`venda` `v` on((`o`.`idOrcamento` = `v`.`idOrcamento`))) where (`o`.`status` not in ('REPLICADO','CANCELADO')) group by `l`.`descricao`,`u`.`user` order by `l`.`descricao`,`u`.`user`;

